    protected void datatable(){
    Object[] Baris ={"NIK","Nama Karyawan","Jabatan","No. Handphone","Alamat"};
    tabmode = new DefaultTableModel(null, Baris);
    String cariitem=txtcarik.getText();

    try {
        String sql = "SELECT * FROM karyawan where nik like '%"+cariitem+"%' or nmkaryawan like '%"+cariitem+"%' order by nik asc";
        Statement stat = conn.createStatement();
        ResultSet hasil = stat.executeQuery(sql);
        while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4),
        hasil.getString(5)
        });
    }
        tblkaryawan.setModel(tabmode);
    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
        }
    }
    
    protected void autonumber(){
        try {
            String sql = "SELECT nik FROM karyawan order by nik asc";
            Statement st = conn.createStatement();
            ResultSet rs = st.executeQuery(sql);
            txtnik.setText("KRY0001");
            while (rs.next()) {
                String id_karyawan = rs.getString("nik").substring(3);
                int AN = Integer.parseInt(id_karyawan) + 1;
                String Nol = ""; 
 
                  if(AN<10)
                  {Nol = "000";}
                  else if(AN<100)
                  {Nol = "00";}
                  else if(AN<1000)
                  {Nol = "0";}
                  else if(AN<10000)
                  {Nol = "";} 
 
                  txtnik.setText("KRY" + Nol + AN);
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, "Auto Number Gagal" +e);
        }
    }

