    private void bsimpankActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpankActionPerformed
        // TODO add your handling code here:
        String sql = "insert into karyawan values (?,?,?,?,?)";
         try {
             PreparedStatement stat = conn.prepareStatement(sql);
             stat.setString(1,txtnik.getText());
             stat.setString(2,txtnmk.getText());
             stat.setString(3,cbjabatan.getSelectedItem().toString());
             stat.setString(4,txthpk.getText());
             stat.setString(5,txtalamatk.getText());
             
             stat.executeUpdate();
             JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
             kosong();
             autonumber();
             txtnmk.requestFocus();
         }
         catch (SQLException e) {
             JOptionPane.showMessageDialog(null, "Data Gagal Disimpan "+e);
         }
         datatable();   
    }//GEN-LAST:event_bsimpankActionPerformed

    private void bubahkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bubahkActionPerformed
        // TODO add your handling code here:
        try{
            String sql = "update karyawan set nmkaryawan=?,jabatan=?,nohpk=?,alamatk=? where nik ='"+txtnik.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1,txtnmk.getText());
            stat.setString(2,cbjabatan.getSelectedItem().toString());
            stat.setString(3,txthpk.getText());
            stat.setString(4,txtalamatk.getText());
 
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            kosong();
            autonumber();
            txtnmk.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Diubah"+e);
        }
        datatable(); 

    }//GEN-LAST:event_bubahkActionPerformed

    private void bhapuskActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapuskActionPerformed
        // TODO add your handling code here:
        int ok = JOptionPane.showConfirmDialog(null,"Apakah Anda Yakin Untuk Menghapus","Konfirmasi Dialog",JOptionPane.YES_NO_OPTION);
         if (ok==0){
             String sql = "delete from karyawan where nik ='"+txtnik.getText()+"'";
             try{
                 PreparedStatement stat = conn.prepareStatement(sql);
                 stat.executeUpdate();
                 JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
                 kosong();
                 autonumber();
                 txtnmk.requestFocus();
             }
             catch (SQLException e){
                 JOptionPane.showMessageDialog(null, "Data Gagal Dihapus"+e);
             }
             datatable();
         }
    }//GEN-LAST:event_bhapuskActionPerformed

