    protected void datatable(){
    Object[] Baris ={"ID Pelanggan","Nama Pelanggan","Jenis Kelamin","No. Handphone","Alamat"};
    tabmode = new DefaultTableModel(null, Baris);
    String cariitem=txtcarip.getText();

    try {
        String sql = "SELECT * FROM pelanggan where idpelanggan like '%"+cariitem+"%' or nmpelanggan like '%"+cariitem+"%' order by idpelanggan asc";
        Statement stat = conn.createStatement();
        ResultSet hasil = stat.executeQuery(sql);
        while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4),
        hasil.getString(5)
        });
    }
        tblpelanggan.setModel(tabmode);
    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
        }
    }
    
    protected void autonumber(){
        try {
            String sql = "SELECT idpelanggan FROM pelanggan order by idpelanggan asc";
            Statement st = conn.createStatement();
            ResultSet rs = st.executeQuery(sql);
            txtid.setText("IN0001");
            while (rs.next()) {
                String id_pelanggan = rs.getString("idpelanggan").substring(2);
                int AN = Integer.parseInt(id_pelanggan) + 1;
                String Nol = ""; 
 
                  if(AN<10)
                  {Nol = "000";}
                  else if(AN<100)
                  {Nol = "00";}
                  else if(AN<1000)
                  {Nol = "0";}
                  else if(AN<10000)
                  {Nol = "";} 
 
                  txtid.setText("IN" + Nol + AN);
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, "Auto Number Gagal" +e);
        }
    }
    


