    private void bsimpanpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanpActionPerformed
        // TODO add your handling code here:
        String jenis = null;
        if(rlaki.isSelected()){
        jenis = "Laki-Laki";
        }else if(rperempuan.isSelected()){
        jenis = "Perempuan";
        }
            String sql = "insert into pelanggan values (?,?,?,?,?)";
    try{
        PreparedStatement stat = conn.prepareStatement(sql);
        stat.setString(1, txtid.getText());
        stat.setString(2, txtnmp.getText());
        stat.setString(3, jenis);
        stat.setString(4, txthpp.getText());
        stat.setString(5, txtalamatp.getText());

        stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "data berhasil disimpan");
        kosong();
        autonumber();
        txtnmp.requestFocus();
    } catch (SQLException e){
        JOptionPane.showMessageDialog(null, "data gagal disimpan"+e);
    }
        datatable();
    }//GEN-LAST:event_bsimpanpActionPerformed

