    private void bubahpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bubahpActionPerformed
        // TODO add your handling code here:
        String jenis = null;
        if(rlaki.isSelected()){
        jenis = "Laki-Laki";
        }else if(rperempuan.isSelected()){
        jenis = "Perempuan";
        }
    try{
        String sql = "update pelanggan set nmpelanggan=?,jenis=?,nohp=?,alamat=? where idpelanggan='"+txtid.getText()+"'";
        PreparedStatement stat = conn.prepareStatement(sql);
        stat.setString(1, txtnmp.getText());
        stat.setString(2, jenis);
        stat.setString(3, txthpp.getText());
        stat.setString(4, txtalamatp.getText());
        stat.executeUpdate();
        JOptionPane.showMessageDialog(null, "data berhasil diubah");
        kosong();
        autonumber();
        txtnmp.requestFocus();
    }
       catch (SQLException e){
        JOptionPane.showMessageDialog(null, "data gagal diubah"+e);
    }
    datatable();
    }//GEN-LAST:event_bubahpActionPerformed

    private void bhapuspActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapuspActionPerformed
        // TODO add your handling code here:
        int ok = JOptionPane.showConfirmDialog(null,"Apakah Anda Yakin Untuk Menghapus","konfirmasi Dialog",JOptionPane.YES_NO_OPTION);
            if (ok==0){
                String sql = "delete from pelanggan where idpelanggan ='"+txtid.getText()+"'";
    try{
        PreparedStatement stat = conn.prepareStatement(sql);
        stat.executeUpdate();
        JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
        kosong();
        autonumber();
        txtnmp.requestFocus();
    }
        catch (SQLException e){
        JOptionPane.showMessageDialog(null, "Data Gagal Dihapus"+e);
    }
        datatable();
    }
    }//GEN-LAST:event_bhapuspActionPerformed

