    protected void datatable(){
    Object[] Baris ={"ID Supplier","Nama Supplier","No. Handphone","Alamat"};
    tabmode = new DefaultTableModel(null, Baris);
    String cariitem=txtcaris.getText();

    try {
        String sql = "SELECT * FROM supplier where idsupplier like '%"+cariitem+"%' or nmsupplier like '%"+cariitem+"%' order by idsupplier asc";
        Statement stat = conn.createStatement();
        ResultSet hasil = stat.executeQuery(sql);
        while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4)
        });
    }
        tblsupplier.setModel(tabmode);
    } catch (Exception e) {
        JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
        }
    }
    
    protected void autonumber(){
        try {
            String sql = "SELECT idsupplier FROM supplier order by idsupplier asc";
            Statement st = conn.createStatement();
            ResultSet rs = st.executeQuery(sql);
            txtids.setText("SPL0001");
            while (rs.next()) {
                String id_supplier = rs.getString("idsupplier").substring(3);
                int AN = Integer.parseInt(id_supplier) + 1;
                String Nol = ""; 
 
                  if(AN<10)
                  {Nol = "000";}
                  else if(AN<100)
                  {Nol = "00";}
                  else if(AN<1000)
                  {Nol = "0";}
                  else if(AN<10000)
                  {Nol = "";} 
 
                  txtids.setText("SPL" + Nol + AN);
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null, "Auto Number Gagal" +e);
        }
    }
