    private void bsimpansActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpansActionPerformed
        // TODO add your handling code here:
        String sql = "insert into supplier values (?,?,?,?)";
        try {
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1,txtids.getText());
            stat.setString(2,txtnms.getText());
            stat.setString(3,txthps.getText());
            stat.setString(4,txtalamats.getText());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosong();
            autonumber();
            txtnms.requestFocus();
        }
        catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan "+e);
        }
        datatable();
    }//GEN-LAST:event_bsimpansActionPerformed

    private void bubahsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bubahsActionPerformed
        // TODO add your handling code here:
        try{
            String sql = "update supplier set nmsupplier=?,nohps=?,alamats=? where idsupplier ='"+txtids.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1,txtnms.getText());
            stat.setString(2,txthps.getText());
            stat.setString(3,txtalamats.getText());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            kosong();
            autonumber();
            txtnms.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Diubah"+e);
        }
        datatable();
    }//GEN-LAST:event_bubahsActionPerformed

    private void bhapussActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapussActionPerformed
        // TODO add your handling code here:
        int ok = JOptionPane.showConfirmDialog(null,"Apakah Anda Yakin Untuk Menghapus","Konfirmasi Dialog",JOptionPane.YES_NO_OPTION);
        if (ok==0){
            String sql = "delete from supplier where idsupplier ='"+txtids.getText()+"'";
            try{
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null, "Data Berhasil Dihapus");
                kosong();
                autonumber();
                txtnms.requestFocus();
            }
            catch (SQLException e){
                JOptionPane.showMessageDialog(null, "Data Gagal Dihapus"+e);
            }
            datatable();
        }
    }//GEN-LAST:event_bhapussActionPerformed

